import React, { useEffect, useState } from 'react';

const Two = () => {

    const [scrollPosition, setScrollPosition] = useState(0);

    useEffect(() => {

        const handleScroll = () => {
            setScrollPosition(window.scrollY);
        };

        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    },  []);


    useEffect(() => {
        const textSection = document.querySelector('.two');

        console.log('pppppppppppppppppp', scrollPosition);
    
        if (textSection) {

            if (scrollPosition > 980 && scrollPosition < 1035 ) {

                console.log('aaaaaaaaaaaaaaaaaaaa', scrollPosition);
                let aa = parseInt(scrollPosition) - 980;   
                textSection.style.transform = "translate(0%, " + aa.toString() +"%)";

                // let aa = parseInt(scrollPosition) - 50;
        
                // textSection.style.transform = "translate(0%, -" + aa.toString() +"%)";
                // // console.log('1111111111', scrollPosition, '----', imageSection.style.transform, '---' ,  'translate(0% , -', aa.toString() ,'%)');
                // } else {
                // console.log('22222222222');
        
                // imageSection.style.transform = 'translate(0%, 0%)';
            } else {
                textSection.style.opacity = '0';
                
            }
        
        }
       
    
    
        // 1035.199951171875
      }, [scrollPosition]);
    


    return (
        <div className="two">
            <div className='content-two'>
                    <h2>Captions App interfaceScroll down</h2>
                    <h1>It starts</h1>
                    <h1>with an idea</h1>
                    <article>Say goodbye to writer's block. Captions simplifies the creative process and helps you elevate your storytelling to new heights.</article>
            </div>
        </div>
    )
}

export default Two;

