                                            <div className='mobile-camera1'>

                                                <div className='mobile-camera'>
                                                    <div className='mobile-in-camera'>
                                                    </div>
                                                </div>
                                            </div>

                                            <div className='mobile-top-text'>AI - Caption</div>
                                        </div>
                                    </div>
                                    <div className='mobile-body'>
                                        {/* <iframe src={AnimationVideo} className='home-video'     allow="autoplay; fullscreen"></iframe> */}
                                        <video className='home-video' controls autoplay>
                                                <source src={AnimationVideo} type="video/mp4" />
                                        </video>
                                    </div>

                                    <div className='mobile-bottom'>
