import React, { useState, useEffect } from 'react';
import AnimationVideo from '../assert/video/video1.mp4';
import QRCode from '../assert/img/qr-code.png';


const MainCaption = () => {


    const [scrollPosition, setScrollPosition] = useState(0);
    const [moveOne, setMoveOne] = useState(0);

     

    useEffect(() => {

        const handleScroll = () => {
            setScrollPosition(window.scrollY);
        };

        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    },  []);

  useEffect(() => {
    const imageSection = document.querySelector('.video-section');
    const imageContainer = document.querySelector('.video-container .mobile-panel');

    // console.log('aaaaaa', aa.toString());
    
    console.log('-----------------',scrollPosition );

    if (imageSection) {

 if (scrollPosition > 50 && scrollPosition <= 350 ) {
        let aa = (parseInt(scrollPosition) - 50) * 0.1;

        imageSection.style.transform = "translate(0%, -" + aa.toString() +"%)";
        // console.log('1111111111', scrollPosition, '----', imageSection.style.transform, '---' ,  'translate(0% , -', aa.toString() ,'%)');
      } else {
        // console.log('22222222222');

        // imageSection.style.transform = 'translate(0%, 0%)';
      }
    }

    // 188 -170
     if (scrollPosition > 210 && scrollPosition <= 1705){
        let www = (parseInt(scrollPosition) * 0.0001) + 1;
        let www1 = (parseInt(scrollPosition) * 0.00005) + 1;
        console.log('JJJJJJJJJJJJJJ', www, www1);
        setMoveOne(www1);

        // (300 + (parseInt(scrollPosition) - 150) * 2);
        imageContainer.style.transform   = 'scale('+ www.toString() + ',' + www1.toString() + ')';
        // console.log('yyyyyyyyyyyyy',scrollPosition,  www, www1, '------------', imageContainer.style.transform)

    } else {
        imageContainer.style.transform   = 'scale(1, 1)';

    }




  }, [scrollPosition]);


  useEffect(() => {
    const imageSection = document.querySelector('.image-section');

    if (scrollPosition >= 130) {
    if (scrollPosition >= 130 && scrollPosition <= 250) {
        let aa = parseInt(scrollPosition) - 130;
        imageSection.style.transform = "translate(0%, -" + aa.toString() +"%)";
        imageSection.style.opacity = '1'; }

    } else {
        imageSection.style.opacity = '0';
    }


  }, [scrollPosition]);

    return (
        <div className="main-caption">
            <div className='main-container'>
                <div className="video-section">
                    <div className="video-container">
                        {/* <iframe src={AnimationVideo} className='home-video' ></iframe> */}
                        <div className='mobile-panel'>
                            <div className='mobile-in-panel'>
                                <div className='mobile-top'>
                                    <div className='mobile-camera'>
                                        <div className='mobile-in-camera'>
                                        </div>
                                    </div>
                                    <div> <svg xmlns="http://www.w3.org/2000/svg" fill='wite' viewBox="0 0 32 32"><g data-name="20-Arrow Left"><path d="M16 0a16 16 0 1 0 16 16A16 16 0 0 0 16 0zm0 30a14 14 0 1 1 14-14 14 14 0 0 1-14 14z"/><path d="m8.41 15 5.29-5.29-1.41-1.42-7 7a1 1 0 0 0 0 1.41l7 7 1.41-1.41L8.41 17H27v-2z"/></g></svg></div>
                                    <div><p>Select</p></div>

                                </div>
                            </div>
                        </div>

                    </div>
                </div>
                <div className='image-section'>
                    <img src={QRCode} alt="Home Page Image"  className="qrcode-image"/>
                </div> 
            </div>
        </div>
    )
}

export default MainCaption;
