import React, { useState, useEffect } from 'react';
import homeVideo from '../assert/video/video1.mp4';


const One = () => {


    const [scrollPosition, setScrollPosition] = useState(0);

  useEffect(() => {

    const handleScroll = () => {
      setScrollPosition(window.scrollY);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  useEffect(() => {
    const imageSection = document.querySelector('.image-section');

    console.log('-----------------',scrollPosition );

    if (imageSection) {
      if (scrollPosition >= 500) {
        imageSection.style.transform = 'translate(0%, -'+ scrollPosition - 500 +'%)';
        console.log('1111111111', scrollPosition, 'translate(0%, -'+ scrollPosition - 500 +'%)');
      } else {
        console.log('22222222222');

        imageSection.style.transform = 'translate(0%, 1%)';
      }
    }
  }, [scrollPosition]);


  useEffect(() => {
    const imageContainer = document.querySelector('.image-container');



    // if (scrollPosition >= 200) {
    //     imageContainer.style.transform = 'translate(-50%, -100%)';
    //   } else {
    //     imageContainer.style.transform = 'translate(-50%, 0)';
    //   }
  }, [scrollPosition]);

    return (
        <div className="one">
            <div className="image-section">
                <div className="image-container">

                {/* <img src={homeLogo} alt="Home Page Image"  className="home-image"/> */}
                <iframe src={homeVideo}  ></iframe>
                </div>
            </div>
        </div>
    )
}

export default One;
